import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

import { List, ListItem } from 'Lib/List'
import { SectionHeader } from 'Lib/Text'
import { formatCurrency, isEmpty } from 'Utils'

const CURRENCY = 'INR'
class FareBreakup extends PureComponent {
  render() {
    return (
      <div>
        <SectionHeader subheader={this.props.titleSubHeader}>
          Fare Breakup
        </SectionHeader>
        <List>
          <If condition={!isEmpty(this.props.fares)}>
            {this.props.fares.map((item, index) => {
              return (
                <If condition={item.amount > 0}>
                  <ListItem key={index} className="h-48 flex flex-middle flex-between">
                    <p
                      className={classNames('fs-15 c-black-90', {
                        'fw-700': item.isBold
                      })}
                    >
                      {item.type}
                    </p>
                    <p
                      className={classNames('fs-15 c-black-90', {
                        'fw-700': item.isBold
                      })}
                    >
                      {item.type === 'Discount' ? '-' : ''}{' '}
                      {formatCurrency(item.amount)}
                    </p>
                  </ListItem>
                </If>
              )
            })}
          </If>
        </List>
      </div>
    )
  }
}

FareBreakup.propTypes = {
  titleSubHeader: PropTypes.string,
  fares: PropTypes.arrayOf(PropTypes.any)
}

FareBreakup.defaultProps = {
  titleSubHeader: '',
  fares: []
}

export default FareBreakup
