import React from 'react'
import PropTypes from 'prop-types'
import classnames from "classnames";

const FilterWrapper = props => {
  let classes = classnames( props.show
    ? 'filter__wrapper filter__wrapper--active'
    : 'filter__wrapper', props.className)

  if (props.showAnimationFlag) {
    classes =
      'filter__wrapper filter__wrapper--active filter__wrapper--animation'
  }

  return <div className={classes}>{props.children}</div>
}

FilterWrapper.propTypes = {
  children: PropTypes.element,
  show: PropTypes.bool,
  showAnimationFlag: PropTypes.bool
}

FilterWrapper.defaultProps = {
  show: true,
  showAnimationFlag: false
}
export default FilterWrapper
