import React from 'react'
import { ListItem } from 'Lib/List'
import { constructHotelDetailsImageUrl } from 'Utils/hotels'

const HotelSummary = props => {
  const { name, city, duration, hotelImageURL } = props
  const URL = constructHotelDetailsImageUrl(
    hotelImageURL,
    `f_auto,q_auto,c_fill`
  )
  return (
    <ListItem
      className="h-56 flex flex-middle flex-between pt-5 pb-5"
      enableRipple={true}
      onClick={props.onClick}
    >
      <div className="flex flex-top">
        <img src={URL} width="34" height="34" />
        <div className="flex pl-16">
          <div className="flex flex-column">
            <p className="fs-15 c-black-80 mb-3">{name}</p>
            <p className="fs-13 c-black-50 mb-3">
              {city}, {duration}
            </p>
          </div>
        </div>
      </div>
    </ListItem>
  )
}

HotelSummary.displayName = 'HotelSummary'

export default HotelSummary
