import React, { PureComponent } from 'react'
import InputBlock from 'Lib/Form/InputBlock'
import PropTypes from 'prop-types'
import { Shake } from 'Lib/Animate'

class Location extends PureComponent {
  _handleDestinationSearch = () => {
    this.props.showDestinationSearch()
  }
  render() {
    return (
      <Shake animate={this.props.animate}>
        <InputBlock
          label="Destination"
          fieldClass="TouchAndGo__tappable--row-large"
          labelClass="fs-15 mb-10 c-black-80 "
          value={this.props.value}
          valueClass="fs-30 fw-300 t-truncate h-32"
          showEmptyValue={true}
          onClick={this._handleDestinationSearch}
          hasError={this.props.animate}
        />
      </Shake>
    )
  }
}

Location.propTypes = {
  showDestinationSearch: PropTypes.func,
  value: PropTypes.string,
  animate: PropTypes.bool
}

Location.defaultProps = {
  showDestinationSearch: () => {},
  value: 'Bangalore',
  animate: false
}

export default Location
