import React from 'react'
import PropTypes from 'prop-types'

import LocationIcon from './icons/location.svg'
import CloseIcon from './icons/close.svg'

const LocationBar = props => {
  return (
    <div className="Localilty flex flex-middle pl-16 pr-16">
      <LocationIcon width="20" height="20" fill="#3366cc" />
      <div className="pl-8 flex-1">
        <p className="fs-13 c-black-40 lh-copy">
          Showing hotels by distance from
        </p>
        <p className="t-truncate fs-14 fw-500 c-black-80 lh-copy mt--5">
          {props.address}
        </p>
      </div>
      <CloseIcon
        width="17"
        height="17"
        fill="#b3b3b3"
        onClick={props.onClose}
      />
    </div>
  )
}

LocationBar.propTypes = {
  onClose: PropTypes.func,
  address: PropTypes.String
}

LocationBar.defaultProps = {
  onClose: () => {},
  address: ''
}

export default LocationBar
