import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'

import Rooms from 'components/Hotels/Rooms'

class TravellerDetails extends PureComponent {
  state = {}

  render() {
    const roomsData = this.props.roomsDetails
    return (
      <Rooms
        showingTravellerSheet={this.props.showingTravellerSheet}
        toggleTravellerSheet={this.props.toggleTravellerSheet}
        rooms={roomsData}
        saveToSearch={this.props.saveToSearch}
      />
    )
  }
}

TravellerDetails.propTypes = {
  showingTravellerSheet: PropTypes.bool,
  toggleTravellerSheet: PropTypes.func,
  roomsDetails: PropTypes.arrayOf(PropTypes.any),
  saveToSearch: PropTypes.func
}

TravellerDetails.defaultProps = {
  showingTravellerSheet: false,
  toggleTravellerSheet: () => {},
  roomsDetails: [],
  saveToSearch: () => {}
}

export default TravellerDetails
