import React from 'react'
import PropTypes from 'prop-types'
import { SectionHeader } from 'Lib/Text'
import { List, ListItem } from 'Lib/List'
import { AvailabiltyBadge } from 'Lib/Badges'
import Stencil from 'Lib/Stencil'

const _renderItems = count => {
  return Array.apply(0, Array(count)).map((iterator, index) => {
    return (
      /* eslint-disable react/no-array-index-key */
      <ListItem className="h-48 flex flex-middle flex-between" key={index}>
        <Stencil width="90" height="15" shimmer={true} type="rounded" />
        <Stencil width="40" height="13" shimmer={true} type="rounded" />
        <AvailabiltyBadge />
      </ListItem>
    )
  })
}

const Placeholder = props => {
  return (
    <div>
      <SectionHeader>Searched For</SectionHeader>
      <List>
        <ListItem className="h-48 flex flex-middle flex-between">
          <Stencil width="90" height="15" shimmer={true} type="rounded" />
          <Stencil width="40" height="13" shimmer={true} type="rounded" />
          <AvailabiltyBadge />
        </ListItem>
      </List>

      <SectionHeader>Other Dates</SectionHeader>
      <List>{_renderItems(props.count)}</List>
    </div>
  )
}

Placeholder.propTypes = {
  count: PropTypes.number
}

Placeholder.defaultProps = {
  count: 10
}

Placeholder.displayName = 'Placeholder(TrainsAvailability)'

export default Placeholder
