import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'

import Clock from './clock.svg'

class TimeBlock extends PureComponent {
  render() {
    return (
      <div key="time-block" className="ta-c ml-15 mr-15">
        <div>
          <Clock fill="#929292" />
        </div>
        <span className="fs-11 c-black-40">{this.props.time}</span>
      </div>
    )
  }
}

TimeBlock.propTypes = {
  time: PropTypes.string
}

TimeBlock.defaultProps = {
  time: ''
}

export default TimeBlock
