import React from 'react'
import PropTypes from 'prop-types'
import { List, ListItem } from 'Lib/List'
import Stencil from 'Lib/Stencil'

const _renderItems = count => {
  return Array.apply(0, Array(count)).map((x, i) => {
    return (
      /* eslint-disable react/no-array-index-key */
      <ListItem className="h-64 flex flex-middle" key={i}>
        <div className="d-b w-100p">
          <div className="flex flex-between mb-5 mt-5">
            <Stencil width="140" height="13" shimmer={true} />
            <Stencil width="40" height="13" shimmer={true} />
          </div>
          <div className="flex flex-between mb-5">
            <Stencil width="90" height="10" shimmer={true} />
            <Stencil width="80" height="10" shimmer={true} />
          </div>
        </div>
      </ListItem>
    )
  })
}

const Placeholder = props => {
  return <List className="mt-10">{_renderItems(props.count)}</List>
}

Placeholder.propTypes = {
  count: PropTypes.number
}

Placeholder.defaultProps = {
  count: 10
}

Placeholder.displayName = 'Placeholder(TrainsResults)'

export default Placeholder
