import React from 'react'
import PropTypes from 'prop-types'
import classnames from 'classnames'
import SeatArrow from './icons/seat_arrow.svg'

const LowSeat = ({
  lowSeatLabel,
  className,
  label,
  labelPlural,
  labelCount,
  arrowStyle
}) => {
  const lowSeatLabelText = parseInt(lowSeatLabel, 10) > 1 ? label : labelPlural
  return (
    <Choose>
      <When condition={arrowStyle}>
        <div className="SeatLeft">
          <div className="p-relative">
            <span className="seatCount">{labelCount}</span>
            <SeatArrow fill="#df3b2e" />
          </div>

          <p className="fs-6 c-red mt-3 fw-600">{lowSeatLabelText}</p>
        </div>
      </When>
      <Otherwise>
        <p className={classnames('LowSeat', className)}>
          {labelCount} {lowSeatLabelText}
        </p>
      </Otherwise>
    </Choose>
  )
}

LowSeat.propTypes = {
  className: PropTypes.string,
  lowSeatLabel: PropTypes.string.isRequired,
  label: PropTypes.string,
  labelPlural: PropTypes.string,
  labelCount: PropTypes.string,
  arrowStyle: PropTypes.bool
}

LowSeat.defaultProps = {
  className: '',
  label: 'seat left at',
  labelPlural: 'seats left at',
  labelCount: '1',
  arrowStyle: false
}

LowSeat.displayName = 'LowSeat'

export default LowSeat
