import React from 'react'
import PropTypes from 'prop-types'
import classnames from 'classnames'

import SetInnerHTML from 'Lib/SetInnerHTML'
import DealIcon from './icons/deal.svg'
import FlashSaleIcon from './icons/flash_sale.svg'

const Deal = props => {
  return (
    <div
    className={classnames('Deal pl-16 pr-16', props.className)}
    onClick={props.onClick}
  >
  <Choose>
      <When condition={!props.isEnableFlashSaleBanner}>
        <div className="Deal__logo">
          <DealIcon className="Deal__icon" />
        </div>
      </When>
      <Otherwise>
        <div className="Deal__flashSale-logo">
          <FlashSaleIcon className="Deal__flashSale-icon" />
        </div>
      </Otherwise>
    </Choose>
    <SetInnerHTML className="Deal__copy" innerHTML={props.message} />
  </div>
  )
}

Deal.displayName = 'Deal'

Deal.propTypes = {
  className: PropTypes.string,
  message: PropTypes.string,
  onClick: PropTypes.func,
  isEnableFlashSaleBanner: PropTypes.bool
}

Deal.defaultProps = {
  className: '',
  message: '',
  onClick: () => {},
  isEnableFlashSaleBanner: false
}

export default Deal
