import React from "react";
import classnames from "classnames";

const ImageCard = (props) => {
  const customStyle = {
    backgroundColor: "#333",
    backgroundImage: `url('${props.imgUrl}')`,
    backgroundSize: "cover",
    height: "192px",
    position: "relative",
    overflow: "hidden"
  };
  const onclickHandler = (props) => {
    props.onClick();
  };
  return (
    <div
      style={ customStyle }
      className={ classnames("w-100p", props.className) }
      onClick={ onclickHandler.bind(null, props) }
    >
      <div
        className="p-absolute w-100p"
        style={ {
          background: "linear-gradient(to bottom, rgba(0,0,0,0), rgba(0,0,0,.8))",
          height: "40px",
          bottom: 0
        } }
      >
        <p className="c-white fs-13 ta-r pr-16 pb-16 mt-15">
          <span>{props.index}</span> of <span>{props.total}</span>
        </p>
      </div>
    </div>
  );
};

export default ImageCard;
