import React, { PureComponent } from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { Tooltip } from "react-tippy";

import Icon from "./icons/info.svg";

class InfoIcon extends PureComponent {
  handleClick = () => {
    if (this.props.viewCancellationPolicyClicked) {
      this.props.viewCancellationPolicyClicked();
    }
  }
  render() {
    const { content, className } = this.props;

    return (
      <Tooltip
        style={ {
          display: "inline-flex"
        } }
        html={
          <ul
            className="lh-copy ta-l fs-13 pl-10 pt-5 pb-5 pr-10"
            style={ { listStyleType: "disc" } }
          >
            {content.map((el, index) => {
              return <li key={ index }>{el}</li>;
            })}
          </ul>
        }
        arrow={ true }
        position="top"
        sticky={ true }
        trigger="click"
        delay={ 0 }
      >
        <Icon
          height="13"
          width="13"
          fill="rgb(51,102,204)"
          className={ classNames(className) }
          onClick={ this.handleClick }
        />
      </Tooltip>
    );
  }
}

InfoIcon.propTypes = {
  content: PropTypes.string,
  className: PropTypes.string
};

InfoIcon.defaultProps = {
  content: "",
  className: ""
};

export default InfoIcon;
