import React, { Component } from "react";
import PropTypes from "prop-types";
import classNames from "classnames";

import AddReadMore from "Lib/AddReadMore";

class ReadMore extends Component {
  state = {
    showMore: false
  }

  handleShowMore = () => {
    if (this.props.type === "reviews") {
      this.props.onClick();
    } else {
      this.setState({
        showMore: true
      });
      this.props.readMoreAboutHotelClicked();
    }
  }

  render() {
    const className = classNames({
      ReadMore__content: !this.state.showMore
    });

    const readMoreClass = classNames("fs-13 c-blue ta-l", this.props.className);
    return (
      <div className="ReadMore" onClick={this.handleShowMore}>
        <div className={className}>{this.props.children}</div>
        <If condition={!this.state.showMore}>
          <p className={readMoreClass}>Read More...</p>
        </If>
      </div>
    );
  }
}

export default ReadMore;
