import React from 'react'
import ReactSlider from "react-slick";

const Slider = props => {

  const settings = {
    className: 'center',
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    centerPadding: '16px',
    swipe: true,
    swipeToSlide: true,
    accessibility: false,
    lazyLoading: true,
    centerMode: true,
    arrows : false
  };

  return (
    <ReactSlider
      {...settings}
      {...props.settings}
    >
      {props.children}
    </ReactSlider>
  )
}

export default Slider