import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'
import { ListItem } from 'Lib/List'

import Tick from './icons/tick.svg'


class SortList extends PureComponent {
	_handleSortingChange = () => {
		const { value, order, handleSortingChange } = this.props;
		handleSortingChange(value, order);
	}
	render() {
		const { label, isActive } = this.props;

		return (
			<ListItem
				className="ta-l pt-10 pb-10 pl-8 pr-16 flex flex-between flex-middle"
				enableRipple={true}
				onClick={this._handleSortingChange}
			>
				<p className={'fs-14 lh-copy ' + (isActive ? 'c-blue' : 'c-black-80')}>{label}</p>
				{
					isActive ?
						<Tick fill="#36c" />
						: ''
				}

			</ListItem>
		)
	}
}

SortList.propTypes = {
	label: PropTypes.string,
	closeFn: PropTypes.func,
	isActive: PropTypes.boolean
}

SortList.defaultProps = {
	closeFn: () => { },
	isActive: false
}

export default SortList

