import React, { Component } from "react";
import PropTypes from "prop-types";
import classNames from "classnames";

class ViewMore extends Component {
  state = {
    showMore: false
  }

  handleShowMore = () => {
    if(this.props.type==='reviews'){
      this.props.onClick()
    } else{
      this.setState({
        showMore: true
      });
    }
  }

  render() {
    const className = classNames({
        ViewMore__content: !this.state.showMore
    });

    const readMoreClass = classNames(
      'fs-13 c-blue ta-l',
      this.props.className
    )
    return (
      <div className="ViewMore" onClick={ this.handleShowMore }>
        <div className={ className }>{this.props.children}</div>
        <If condition={ !this.state.showMore }>
          <p className="c-light-blue fs-16 lh-title fw-500 ta-c pt-12 pb-12">View more</p>
        </If>
      </div>
    );
  }
}

export default ViewMore;
