import React from 'react'

import Header from 'Lib/Header'
import Sticky from 'Lib/Sticky'

import ConfirmationContainer from 'Containers/Hotels/Confirmation'

import { path } from 'Utils'

const ConfirmationPage = props => {
  const itineraryId = path(['params', 'id'], props)
  return (
    <div>
      <Sticky>
        <Header menu={true} />
      </Sticky>
      <ConfirmationContainer itineraryId={itineraryId} />
    </div>
  )
}

ConfirmationPage.displayName = 'Hotels(ConfirmationPage)'

export default ConfirmationPage
