import React from 'react'
import PropTypes from 'prop-types'

import PaymentContainer from 'Containers/Hotels/Payment'

const Payment = props => {
  const { isRetry } = props.route
  return <PaymentContainer itineraryId={props.params.id} isRetry={isRetry} />
}

Payment.propTypes = {
  route: PropTypes.objectOf(PropTypes.oneOfType([PropTypes.any])).isRequired,
  params: PropTypes.objectOf(PropTypes.any).isRequired
}

Payment.displayName = 'Hotels(PaymentsPage)'

export default Payment
