import React from 'react'
import PropTypes from 'prop-types'

import TravellerContainer from 'Containers/Hotels/Traveller'

const Traveller = props => {
  return <TravellerContainer itineraryId={props.params.id} />
}

Traveller.propTypes = {
  params: PropTypes.objectOf(PropTypes.any).isRequired
}

Traveller.displayName = 'Hotels(TravellerPage)'

export default Traveller
