import React from 'react'
import PropTypes from 'prop-types'

import Header from 'Lib/Header'
import Sticky from 'Lib/Sticky'

import ConfirmationContainer from 'Containers/Trains/Confirmation'

const TrainConfirmation = props => {
  return (
    <div>
      <Sticky>
        <Header menu={true} />
      </Sticky>
      <ConfirmationContainer itineraryId={props.params.id} />
    </div>
  )
}

TrainConfirmation.propTypes = {
  params: PropTypes.objectOf(PropTypes.any).isRequired
}

TrainConfirmation.displayName = 'Trains(ConfirmationPage)'

export default TrainConfirmation
