import React from 'react'
import PropTypes from 'prop-types'

import Header from 'Lib/Header'
import { Pagetitle } from 'Lib/Subheader'

import PaymentContainer from 'Containers/Trains/Payment'

const Payment = props => {
  const { isRetry } = props.route
  const pageTitle = isRetry ? 'Retry payment' : 'Make payment'
  return (
    <div>
      <Header menu={true} />
      <Pagetitle title={pageTitle} />
      <PaymentContainer itineraryId={props.params.id} isRetry={isRetry} />
    </div>
  )
}

Payment.propTypes = {
  route: PropTypes.objectOf(PropTypes.oneOfType([PropTypes.any])).isRequired,
  params: PropTypes.objectOf(PropTypes.any).isRequired
}

Payment.displayName = 'Trains(PaymentPage)'

export default Payment
